/*FUNÇÃO PARA CONFIRMAR SENHA */

function validarSenha(name1, name2){
    var senha1 = document.getElementById(name1).value;
    var senha2 = document.getElementById(name2).value;
        
    if (senha1 != "" && senha2 != "" && senha1 === senha2){}
    else{
        alert('Senhas digitadas estão diferentes, por favor insira novamente');
    }
}

/* PESQUISA */

function pesquisar() {
    var input, filtro, menu, itens, links;
    input = document.getElementById("pesquisa");
    menu = document.getElementById("catalogo");
    menuitens = menu.document.getElementsByClassName("card");

    for (var i=0;i<menuitens.length;i++);
        links = menuitens.getElementByTagName("a");
        if(links.innerHTML.toUpperCas().indexOf(filtro)>-1){
            menuitens[i].style.display="";
        } else {
            menuitens[i].style.display="none";
        }
}

